import actorsService from '../services/actors'

const singleMovieReducer = (state = [], action) => {
	switch(action.type) {
		case 'GET_ACTORS':
      return action.data
	default: return state
	}
}

export const fetchActors = (id) => {
  return async dispatch => {
    const actors = await actorsService.getActors(id)
    dispatch({
      type: 'GET_ACTORS',
      data: actors.data
    })
  }
}


export default singleMovieReducer
