import movieService from '../services/movies'

const singleMovieReducer = (state = [], action) => {
	console.log(action)
	switch(action.type) {
		case 'INIT_SINGLE_MOVIE':
      return action.data
		case 'GET_GENRES':
			return action.data
	default: return state
	}
}
console.log(singleMovieReducer.state)

export default singleMovieReducer
